import pytest
from questions.q_0005_LongestPalindromicSubstring import Solution


@pytest.mark.parametrize(
    "s, output",
    [
        ("babad", "aba"),
        ("cbbd", "bb"),
        ("aaaaa", "aaaaa"),
        (
            "",
            "oxaxo",
        ),
        (
            "",
            "",
        ),
        ("ac", "a"),
        (
            "",
            "xllx",
        ),
    ],
)
class TestSolution:
    def test_longestPalindrome(self, s: str, output: str):
        sc = Solution()
        assert (
            sc.longestPalindrome(
                s,
            )
            == output
        )
